package com.lbs.apps.common;/** *  *  * 业务处理层总异常 *  *  * @author chenshuichao *  *  *  *  *   */public class ApplicationException extends Exception {	public ApplicationException() {		super();	}	public ApplicationException(String msg) {		super(msg);	}	public ApplicationException(String msg, Throwable cause) {		super(msg, cause);	}	public ApplicationException(Throwable cause) {		super(cause);	}}